document.addEventListener("DOMContentLoaded",()=>{const mobileToggle=document.querySelector(".mobile-toggle");const navLinks=document.querySelector(".nav-links");const body=document.body;if(mobileToggle){mobileToggle.addEventListener("click",()=>{navLinks.classList.toggle("active");mobileToggle.classList.toggle("active");const bars=mobileToggle.querySelectorAll(".bar");if(navLinks.classList.contains("active")){bars[0].style.transform="rotate(-45deg) translate(-5px, 6px)";bars[1].style.opacity="0";bars[2].style.transform="rotate(45deg) translate(-5px, -6px)";body.style.overflow="hidden"}else{bars[0].style.transform="none";bars[1].style.opacity="1";bars[2].style.transform="none";body.style.overflow="visible"}})}document.querySelectorAll(".nav-links a").forEach(link=>{link.addEventListener("click",()=>{navLinks.classList.remove("active");const bars=mobileToggle.querySelectorAll(".bar");bars[0].style.transform="none";bars[1].style.opacity="1";bars[2].style.transform="none";body.style.overflow="visible"})});const navbar=document.querySelector(".navbar");window.addEventListener("scroll",()=>{if(window.scrollY>50){navbar.classList.add("scrolled")}else{navbar.classList.remove("scrolled")}});const observerOptions={threshold:.1};const observer=new IntersectionObserver(entries=>{entries.forEach(entry=>{if(entry.isIntersecting){entry.target.style.opacity="1";entry.target.style.transform="translateY(0)";observer.unobserve(entry.target)}})},observerOptions);document.querySelectorAll(".section-title").forEach(el=>{el.style.opacity="0";el.style.transform="translateY(20px)";el.style.transition="all 0.6s ease-out";observer.observe(el)});const heroVideo=document.querySelector("video");if(heroVideo){heroVideo.play().catch(error=>{console.log("Video autoplay failed:",error);heroVideo.controls=false})}});